import React, { useState, useEffect} from 'react';
// import NavigationBar from './Header';
import { Button, Form, Alert } from 'react-bootstrap';
import './styles/Login.css';
import { useMutation, gql } from '@apollo/client';
import { useNavigate  } from 'react-router-dom';
import { ImageLogo } from './ImageLogo.js'

const SIGN_UP_MUTATION = gql`
mutation signUp(
  $email: String!, 
  $password: String!, 
  $name: String!) {
  signUp(input: {
    email: $email,
    password: $password,
    name: $name
  }) {
    token
    user {
      id
      name
    }
  }
}

`;


function SignUp() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
  
  
    const [signUp, { data, loading, error }] = useMutation(SIGN_UP_MUTATION);
    const navigate  = useNavigate();
  
    const handleSignUp = () => {
      signUp({
        variables: { email, password, name },
        
      })
        .then((response) => {
          // Handle the response as needed
          console.log(response);  
          const token = response.data.signUp.token;
          localStorage.setItem('token', token);
  
          navigate('/');
        })
        .catch((error) => {
          // Handle any errors that occurred during the mutation
          console.log(error);
        });
    };
    
  
    return (
      <div className="col-lg-6 form-wrap mx-auto">
      <div className="bd-reg-login text-center" id="bd-ma-login">
        <ImageLogo />
        {/* <form> */}
          <h1 className="h3 mb-3 fw-normal">Place where answer exist</h1>

          {error && (
                <Alert variant="danger">
                    <Alert.Heading>An error occurred:</Alert.Heading>
                    <p>{error.message}</p>
                </Alert>
            )}
          <Form.Group className="mb-3" controlId="floatingname">
            <Form.Control
              type="text"
              className="form-control"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="floatingEmail">
            <Form.Control
              type="email"
              className="form-control"
              placeholder="Email address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="floatingPassword">
            <Form.Control
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>

          <Button
            className="w-100 btn btn-lg btn-primary"
            type="button"
            onClick={handleSignUp}
          >
            Sign Up
          </Button>
        {/* </form> */}
      

        {error && (
          <div className="mt-3">
            <p>An error occurred:</p>
            <p>{error.message}</p>
          </div>
        )}
      </div>
    </div>)
  };


  export { SignUp };
