import React, { useState, useEffect} from 'react';
// import NavigationBar from './Header';
import { Form, Button, Alert } from 'react-bootstrap';
import './styles/Login.css';
import { useMutation, gql } from '@apollo/client';
import { useNavigate  } from 'react-router-dom';
// import { Alert } from 'react-native';
import { ImageLogo } from './ImageLogo.js'

const SIGN_IN_MUTATION = gql`
mutation signIn(
  $email: String!, 
  $password: String!){
  signIn(input: { 
    email: $email, 
    password: $password
  }) {
    token
    user {
      id,
      name,
      email
    }
  }
}
`;

function SignIn() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
  
    const navigate = useNavigate();
  
    const [signIn, { data, loading, error }] = useMutation(SIGN_IN_MUTATION);
  
    useEffect(() => {
      if (error) {
        console.error('Error:', error);
        // Handle the error, e.g., display an error message to the user
      }
    }, [error]);
  
    const onSubmit = async (event) => {
      event.preventDefault();
    
      try {
        const result = await signIn({ variables: { email, password } });
        console.log(result);
        const token = result.data.signIn.token;
        localStorage.setItem('token', token);
        // Navigate to the desired location
        navigate('/');
      } catch (error) {
        console.error('Error:', error);
    
        if (error.message.includes('Invalid credentials')) {
          // Handle the case where the entered password is incorrect
          // You can display an error message to the user
          console.log('Invalid password'); 
        } else {
          // Handle other errors, e.g., display a generic error message
          console.log('An error occurred during sign-in');
        }
      }
    };
    

  return (
    <div className="col-lg-6 form-wrap mx-auto">
      <div className="bd-reg-login text-center" id="bd-ma-login">
      < ImageLogo/>
        <h2 className="login-heading">Hey there! Welcome back.</h2>
        {/* <p className="login-subheading">Login to your account</p> */}
        {error && (
                <Alert variant="danger">
                    <Alert.Heading>An error occurred:</Alert.Heading>
                    <p>{error.message}</p>
                </Alert>
            )}
        <Form className="woocommerce-form woocommerce-form-login login" onSubmit={onSubmit}>
          <Form.Group className="mb-3" controlId="username">
            <Form.Label>Email address<span className="required">*</span></Form.Label>
            <Form.Control
              type="text"
              className="woocommerce-Input woocommerce-Input--text input-text"
              name="email"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="password">
            <Form.Label>Password<span className="required">*</span></Form.Label>
            <span className="password-input">
              <Form.Control
                type="password"
                className="woocommerce-Input woocommerce-Input--text input-text"
                name="password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
              />
            </span>
          </Form.Group>

          <Form.Group className="mb-3" controlId="rememberme">
            <Button
              type="submit"
              className="woocommerce-button button woocommerce-form-login__submit"
              name="login"
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Log in'}
            </Button>
          </Form.Group>

          <p className="woocommerce-LostPassword lost_password text-center">
            
            <a href="https://www.bootstrapdash.com/my-account/lost-password" data-wpel-link="internal">
              Lost your password?
            </a>
          </p>
          <p className="toggle-login text-center">
            <a href="/signUp" id="show-ma-register" data-wpel-link="internal">
              Don’t have an account? Sign up
            </a>
          </p>
        </Form>
      </div>
    </div>
  );
}

export {SignIn};
