import React, { useState } from 'react';
import Header from './Header';
import { Container, Table, Form, Button } from 'react-bootstrap';
import { useQuery, useMutation, useLazyQuery } from '@apollo/client';
import { Link, useNavigate } from 'react-router-dom';
import './styles/Home.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {QuestionSearch } from './QuestionSearch.js'
import { GET_QUESTION_QUERY, GET_SEARCH_QUERY } from './gql/operation.js';
import { Recommendation } from './recommendation.js';

function Home() {
    const navigate = useNavigate();
    const isAuthenticated = () => {
          const token = localStorage.getItem('token');
          return !!token;
        }
    const { loading: questionLoading, error: questionError, data: questionData } = useQuery(GET_QUESTION_QUERY);
  
    if (questionLoading) return <p>Loading...</p>;
    if (questionError) return <p>Error: {questionError.message}</p>;
  
  
    const handleCreateQuestion = () => {
      // Redirect to the "createnewquestion" page
      navigate('/newquestion');
    };


    return (
      <div className='main-page'>
        <Header />
        
        <div>
            <div>
            {/* // there add element of searching you can use bootstrap element */}
            <QuestionSearch />
            </div>
          
        </div>
      </div>
    );
}


export default Home;
