import React, { useState } from 'react';
import Header from './Header.js';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import { useQuery, useMutation, useLazyQuery, useReadQuery } from '@apollo/client';
import { Link, useNavigate } from 'react-router-dom';
import './styles/Home.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { GET_QUESTION_QUERY, GET_MY_PROFILE_QUERY, GET_MY_QUESTIONS_QUERY } from './gql/operation.js';
import { Recommendation } from './recommendation.js';
import "./styles/profile.css"




function ProfileData() {
    const { loading, error, data } = useQuery(GET_MY_PROFILE_QUERY);
    if (loading) return <p>Loading</p>
    if (error) return <p>Error: {error.message}</p>

    const profile_data = data.myProfile;
    return profile_data;
}

function MyQuestion() {
    const { loading, error, data } = useQuery(GET_MY_QUESTIONS_QUERY);
    if (loading) return <p>Loading</p>
    if (error) return <p>Error: {error.message}</p>

    const questionData = data.myQuestion;
    return questionData;

}

function Profile() {
    const profileData = ProfileData();

    const myQuestion = MyQuestion();

    const navigate = useNavigate();
    const isAuthenticated = () => {
        const token = localStorage.getItem('token');
        return !!token;
    }
    const { loading: questionLoading, error: questionError, data: questionData } = useQuery(GET_QUESTION_QUERY);
  
    if (questionLoading) return <p>Loading...</p>;
    if (questionError) return <p>Error: {questionError.message}</p>;
  
    const questions = questionData.getQuestionList;
  
    const handleCreateQuestion = () => {
      // Redirect to the "createnewquestion" page
      navigate('/newquestion');
    };


    return (
      <div className='main-page'>
        <Header />
        <Container fluid>
            <Row>
                <Col xs={6}>
                {/* Содержимое для первого элемента (25%) */}
                <Card>
                    <Card.Header>
                        <h2>User Profile</h2>
                    </Card.Header>
                    <Card.Body>
                        <p class="card-text"><strong>Name:</strong> { profileData.name }</p>
                        <p class="card-text"><strong>Email:</strong> { profileData.email } </p>
                    </Card.Body>
                </Card>
                </Col>
                
                <Col xs={6}>
                {/* Содержимое для второго элемента (25%) */}
                <Recommendation/>
                </Col>

                <Col xs={12}>
                {/* Содержимое для третьего элемента (50%) */}
                
                <Card>
                    <Card.Header>
                        <h2>My Questions</h2>
                        <Button onClick={handleCreateQuestion} variant="primary">
                            Create Question
                        </Button>
                    </Card.Header>
                    <Card.Body>

                    </Card.Body>
                </Card>
                </Col>
            </Row>
        </Container>
      </div>
    );
    
}


export { Profile };
